let volunteers = JSON.parse(localStorage.getItem('volunteers'))|| [];let projectSignups = JSON.parse(localStorage.getItem('projectSignups'))|| [];document.addEventListener('DOMContentLoaded', function(){    initializeApp();});function initializeApp(){        if(window.NavigationModule){        NavigationModule.setupMobileMenu();        NavigationModule.setupScrollToTop();        NavigationModule.setupSmoothScroll();}            if(window.FormsModule){        FormsModule.setupInputMasks();        FormsModule.setupForms();}            if(window.AnimationsModule){        AnimationsModule.animateStats();        AnimationsModule.setupScrollAnimations();        AnimationsModule.setupParallaxEffect();        AnimationsModule.setupCardHoverEffects();}            if(window.ProjectsModule){        ProjectsModule.setupProjectFilters();        ProjectsModule.setupProjectModal();}            initializePageSpecific();}function initializePageSpecific(){    const currentPage = window.location.pathname.split('/').pop();        switch(currentPage){        case 'index.html':        case '':                        break;        case 'cadastro.html':                        break;        case 'projeto.html':                        break;        case 'admin.html':            if(typeof initializeAdmin === 'function'){                initializeAdmin();}            break;}}function formatPhone(event){    let value = event.target.value.replace(/\D/g, '');    if(value.length <= 10){        value = value.replace(/(\d{2})(\d)/, '($1)$2');        value = value.replace(/(\d{4})(\d)/, '$1-$2');}else{        value = value.replace(/(\d{2})(\d)/, '($1)$2');        value = value.replace(/(\d{5})(\d)/, '$1-$2');}    event.target.value = value;}window.closeModal = function(){    if(window.ProjectsModule){        ProjectsModule.closeModal();}};window.scrollToProjects = function(){    if(window.ProjectsModule){        ProjectsModule.scrollToProjects();}};