function setupMobileMenu(){    const navToggle = document.querySelector('.nav-toggle');    const navMenu = document.querySelector('.nav-menu');    const navLinks = document.querySelectorAll('.nav-link');    if(navToggle && navMenu){                navToggle.addEventListener('click',()=>{            navToggle.classList.toggle('active');            navMenu.classList.toggle('active');                                    document.body.style.overflow = navMenu.classList.contains('active')? 'hidden' : '';});                navLinks.forEach(link =>{            link.addEventListener('click',()=>{                navToggle.classList.remove('active');                navMenu.classList.remove('active');                document.body.style.overflow = '';});});                document.addEventListener('click',(e)=>{            if(!navToggle.contains(e.target)&& !navMenu.contains(e.target)){                navToggle.classList.remove('active');                navMenu.classList.remove('active');                document.body.style.overflow = '';}});                window.addEventListener('resize',()=>{            if(window.innerWidth > 1024){                navToggle.classList.remove('active');                navMenu.classList.remove('active');                document.body.style.overflow = '';}});}}function setupScrollToTop(){    const scrollToTopBtn = document.getElementById('scrollToTop');        if(scrollToTopBtn){        let ticking = false;                        function updateScrollButton(){            if(window.pageYOffset > 300){                scrollToTopBtn.classList.add('show');}else{                scrollToTopBtn.classList.remove('show');}            ticking = false;}                        window.addEventListener('scroll',()=>{            if(!ticking){                requestAnimationFrame(updateScrollButton);                ticking = true;}},{passive: true});                scrollToTopBtn.addEventListener('click',()=>{                        scrollToTopBtn.style.transform = 'translateY(0)scale(0.95)';            setTimeout(()=>{                scrollToTopBtn.style.transform = '';}, 150);                                    window.scrollTo({                top: 0,                behavior: 'smooth'});});}}function setupSmoothScroll(){    const links = document.querySelectorAll('a[href^="#"]');    links.forEach(link =>{        link.addEventListener('click', function(e){            e.preventDefault();            const target = document.querySelector(this.getAttribute('href'));            if(target){                target.scrollIntoView({                    behavior: 'smooth',                    block: 'start'});}});});}window.NavigationModule ={    setupMobileMenu,    setupScrollToTop,    setupSmoothScroll};